mapper.select:
SELECT *
FROM tasks
WHERE task_name LIKE '%' #{taskName} '%' AND task_status LIKE '%' #{taskStatus} '%' LIMIT #{pageable.offset}
    , #{pageable.pageSize};

mapper.update:
UPDATE tasks
SET task_name   = #{task.taskName},
    task_status = #{task.taskStatus},
    description = #{task.description}
WHERE id = #{task.id}
